import { Bar } from "../bar.js"

/**
 * using the information in the data generated by the Model class, each bar with its information is
 * generated and pushed into an array, this array is then used to draw the bars
 * 
 * @param {Model} theData 
 * @param {Options} Options 
 * @returns an array of Bars
 */
export function generateBars(theData, Options){
    let bars = [];
    let idCount = 0;
    let colourIndex = 0;
    let barColour;
    let barX = Options.bar.startingX;
    let barZ = Options.bar.startingZ;
    
    for (let i of theData.data) {
        barColour = Options.bar.colours[colourIndex++]
        console.log(barColour)
        for (let j of i){
            bars.push(new Bar(idCount, j, barColour, barX, barZ, Options.bar.width, Options.bar.depth, Options));
            //add the 10 to options, done
            barX += Options.chart.xAxisGap;
            idCount++;
        }
        //add the 10 to options, done

        //fix the y and z anomaly, done
        barZ -= Options.chart.zAxisGap;
        barX = Options.bar.startingX;
    }
    return bars;
}